import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Navbar from "../home/Navbar";
import "../home/Navbar.css";
import "./Card.css";
import "./User.css";
import "./ProgressCircle.css";
import "./Order.css";

function Profile() {
    const { id } = useParams();
    const [user, setUser] = useState(null);
    const [error, setError] = useState("");

    useEffect(() => {
        const fetchUser = async () => {
            try {
                const response = await fetch(`http://localhost:8080/customers/${id}`);
                if (response.ok) {
                    const data = await response.json();
                    setUser(data);
                } else {
                    setError("Kullanıcı bilgileri alınamadı.");
                }
            } catch (error) {
                setError("Bir hata oluştu. Lütfen tekrar deneyin.");
            }
        };

        fetchUser();
    }, [id]);

    const handleLogout = () => {
        window.location.href = 'http://localhost:3000/login';
    };

    if (error) {
        return <div>{error}</div>;
    }

    if (!user) {
        return <div>Yükleniyor...</div>;
    }

    return (
        <div className="profile-page">
            <Navbar />
            
            <div className="card-container">
                <div className="card card-1">
                    <h2>{user.name}</h2>
                    <div className="profile-divider"></div>
                    <div className="user-info">          
                        <div className="user-photo">
                            <img src={require('../assets/defaultpp.png')} alt="User" style={{ width: '175px', height: '175px' }} />
                        </div>
                        <div className="user-details">
                            <p><strong>ID:</strong> {user.id}</p>
                            <p><strong>İsim:</strong> {user.name}</p>
                            <p><strong>Bütçe:</strong> {user.budget}</p>
                            <p><strong>Tür:</strong> {user.type}</p>
                            <p><strong>Harcanan Miktar:</strong> {user.totalSpend}</p>
                        </div>
                    </div>
                </div>
                
                <div className="card card-2">
                    <h2>Ürün Tablosu</h2>
                    <div className="profile-divider"></div>
                    <div className="table-container">
                        <table className="product-table">
                            <thead>
                                <tr>
                                    <th>Ürün</th>
                                    <th>Ürün İsmi</th>
                                    <th>Stok</th>
                                    <th>Fiyat</th>
                                    <th>Miktar</th>
                                    <th>Sipariş Oluştur</th>
                                </tr>
                            </thead>
                            <tbody>
                                {products.map((product, index) => (
                                    <tr key={product.id}>
                                        <td>
                                            <img src={product.image} alt="User" />
                                        </td>
                                        <td>{product.name}</td>
                                        <td>{product.stock}</td>
                                        <td>{product.price}</td>
                                        <td>
                                            <select
                                                value={quantities[index]}
                                                onChange={(e) => handleQuantityChange(index, e.target.value)}
                                                className="quantity-select"
                                            >
                                                <option value="" disabled hidden>Miktar</option>
                                                <option value="1">1</option>
                                                <option value="2">2</option>
                                                <option value="3">3</option>
                                                <option value="4">4</option>
                                                <option value="5">5</option>
                                            </select>
                                        </td>
                                        <td className="order-cell">
                                            <button>Sipariş Ver</button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>

            <div className="card-container-2">
                <div className="card card-3">
                    <div className="progress-header">
                        <h3>Harcanan Miktar</h3>
                        <h3>Temel Öncelik Skoru</h3>
                    </div>
                    <div className="profile-divider"></div>
                    <div className="progress-container">
                        <div className="progress-circle" style={{ '--progress': '75' }} data-progress="75%"></div>
                        <div className="progress-circle" style={{ '--progress': '100' }} data-progress="15"></div>
                    </div>
                </div>
                <div className="card card-4"></div>
            </div>
        </div>
    );
}

export default Profile;
