import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import "../home/Navbar.css";
import "./Card.css";
import "./User.css";
import "./ProgressCircle.css";
import "./Order.css";

function Profile() {
    
    const { userId } = useParams();
    const [user, setUser] = useState(null);
    const [products, setProducts] = useState([]);
    const [error, setError] = useState("");
    const [quantities, setQuantities] = useState([]);

    useEffect(() => {
        setQuantities(products.map(() => ""));
    }, [products]);

    useEffect(() => {
        const fetchUser = async () => {
            try {
                const response = await fetch(`/customers/${userId}`);
                if (response.ok) {
                    const data = await response.json();
                    setUser(data);
                } else {
                    setError("Kullanıcı bilgileri alınamadı.");
                }
            } catch (error) {
                setError("Bir hata oluştu. Lütfen tekrar deneyin.");
            }
        };

        const fetchProducts = async () => {
            try {
                const response = await fetch(`/products`);
                if (response.ok) {
                    const data = await response.json();
                    setProducts(data);
                } else {
                    setError("Ürün bilgileri alınamadı.");
                }
            } catch (error) {
                setError("Bir hata oluştu. Lütfen tekrar deneyin.");
            }
        };

        fetchUser();
        fetchProducts();
    }, [userId], [products]);


    



    // Ürün miktarı değiştiğinde bu fonksiyon çalışacak
    const handleQuantityChange = (index, value) => {
        const newQuantities = [...quantities];
        newQuantities[index] = value;
        setQuantities(newQuantities);
    };

    // Çıkış butonuna tıklandığında çalışacak fonksiyon
    const handleLogout = () => {
        window.location.href = 'http://localhost:3000/login';
    };


    if (error) {
        return <div>{error}</div>;
    }

    if (!user) {
        return <div>Yükleniyor...</div>;
    }

    // Harcanan miktar yüzdesini hesapla
    const maxAmount = 2000;
    const spentPercentage = Math.min((user.totalSpent / maxAmount) * 100, 100);

    let userPoint = 10;
    let userPointPercentage = 65;
    if(user.customerType==="Premium"){
        userPoint = 15;
        userPointPercentage = 100;
    }
    
    return (
        <div className="">
            <nav className="navbar">
                <div className="navbar-left">
                <button className="home-button">Home</button>
                </div>
                <div className="navbar-right">
                <button className="logout-button" onClick={handleLogout}>Çıkış</button>
                </div>
            </nav>
            
            {/* USER BİLGİLER VE ÜRÜNLER*/}
            <div className="card-container">

                <div className="card card-1">
                    <h2>{user.customerName}</h2>
                    <div className="profile-divider"></div>
            
                    <div className="user-info">          
                        <div className="user-photo">
                            <img src={require('../assets/defaultpp.png')} alt="User" style={{ width: '175px', height: '175px' }} />
                        </div>
                        <div className="user-details">
                            <p><strong>ID:</strong> {user.customerID}</p>
                            <p><strong>İsim:</strong> {user.customerName}</p>
                            <p><strong>Bütçe:</strong> {user.budget}</p>
                            <p><strong>Tür:</strong> {user.customerType}</p>
                            <p><strong>Harcanan Miktar:</strong> {user.totalSpent}TL</p>
                        </div>
                    </div>
                </div>
        
                
                <div className="card card-2">
                    <h2>Ürün Tablosu</h2>
                    <div className="profile-divider"></div>
                    <div className="table-container">
                        <table className="product-table">
                        <thead>
                            <tr>
                            <th>Ürün</th>
                            <th>Ürün İsmi</th>
                            <th>Stok</th>
                            <th>Fiyat(TL)</th>
                            <th>Miktar</th>
                            <th>Sipariş Oluştur</th>
                            </tr>
                        </thead>
                        <tbody>
                            {products.map((product, index) => (
                            <tr key={product.id}>
                                <td>
                                    <img src={require('../assets/minecraft.jpeg')} alt="User"  />
                                </td>
                                <td>{product.productName}</td>
                                <td>{product.stock}</td>
                                <td>{product.price}</td>
                                <td>
                                    <select
                                        value={quantities[index]}
                                        onChange={(e) => handleQuantityChange(index, e.target.value)}
                                        className="quantity-select"
                                    >
                                        <option value="" disabled hidden>
                                            Miktar
                                        </option>
                                        <option value="1">1</option>
                                        <option value="2">2</option>
                                        <option value="3">3</option>
                                        <option value="4">4</option>
                                        <option value="5">5</option>
                                    </select>
                                </td>
                                <td className="order-cell">
                                <button>
                                    Sipariş Ver
                                </button>                               
                                </td>
                            </tr>
                            ))}
                        </tbody>
                        </table>
                    </div>
                </div>
        
            </div>

            {/* HARCANAN MİKTAR VE VERİLEN SİPARİŞLER*/}
            <div className="card-container-2">

                <div className="card card-3">
                    <div className="progress-header">
                        <h3>Harcanan Miktar</h3>
                        <h3>Temel Öncelik Skoru</h3>
                    </div>
                    <div className="profile-divider"></div>
                    <div className="progress-container">
                        <div className="progress-circle" style={{ '--progress': `${spentPercentage}` }} data-progress={`${spentPercentage}%`}></div>
                        <div className="progress-circle" style={{ '--progress': `${userPointPercentage}` }} data-progress={`${userPoint}`}></div>
                    </div>
                
                </div>

                <div className="card card-4">
                    <h2>Sipariş Geçmişi</h2>
                    <div className="profile-divider"></div>
                    <div className="table-container">
                        <table className="product-table">
                        <thead>
                            <tr>
                            <th>Ürün</th>
                            <th>Ürün İsmi</th>
                            <th>Miktar</th>
                            <th>Toplam Fiyat</th>
                            <th>Durum</th>
                            <th>Tarih</th>
                            </tr>
                        </thead>
                        <tbody>
                            {orderproducts.map((product) => (
                            <tr key={product.id}>
                                <td>
                                    <img src={product.image} alt="User"  />
                                </td>
                                <td>{product.name}</td>
                                <td>{product.stock}</td>
                                <td>{product.totalprice}</td>
                                <td>{product.status}</td>
                                <td>{product.date}</td>
                            </tr>
                            ))}
                        </tbody>
                        </table>
                    </div>
                </div>

            </div>

        </div>
    );
    }

export default Profile;
